INSERT INTO `books` VALUES (1, '辰东', '遮天', NULL, '完结', '仙侠', 8.8, '冰冷与黑暗并存的宇宙深处，九具庞大的龙尸拉着一口青铜古棺，亘古长存。这是太空探测器在枯寂的宇宙中捕捉到的一幅极其震撼的画面。九龙拉棺，究竟是回到了上古，还是来到了星空的彼岸？一个浩大的仙侠世界，光怪陆离，神秘无尽。热血似火山沸腾，激情若瀚海汹涌，欲望如深渊无止境……登天路，踏歌行，弹指遮天。', NULL);
INSERT INTO `books` VALUES (2, '遥望南山', '妖龙古帝', NULL, '连载', '玄幻', 8.9, '古帝重生，横扫天下！', NULL);
INSERT INTO `books` VALUES (3, '唐家三少', '斗罗大陆4终极斗罗', NULL, '连载', '玄幻', 8.7, '一万年后，冰化了。斗罗联邦科考队在极北之地科考时发现了一个有着金银双色花纹的蛋，用仪器探察之后，发现里面居然有生命体征，赶忙将其带回研究所进行孵化。蛋孵化出来了，可孵出来的却是一个婴儿，和人类一模一样的婴儿，一个蛋生的孩子。', NULL);
INSERT INTO `books` VALUES (4, '辰东', '完美世界', NULL, '完结', '玄幻', 8.6, '一粒尘可填海，一根草斩尽日月星辰，弹指间天翻地覆。群雄并起，万族林立，诸圣争霸，乱天动地。问苍茫大地，谁主沉浮？！一个少年从大荒中走出，一切从这里开始……...', NULL);
INSERT INTO `books` VALUES (5, '天蚕土豆', '大主宰', NULL, '完结', '玄幻', 8.5, '大千世界，位面交汇，万族林立，群雄荟萃，一位位来自下位面的天之至尊，在这无尽世界，演绎着令人向往的传奇，追求着那主宰之路。无尽火域，炎帝执掌，万火焚苍穹。武境之内，武祖之威，震慑乾坤。西天之殿，百战之皇，战威无可敌。北荒之丘，万墓之地，不死之主镇天地。......少年自北灵境而出，骑九幽冥雀，...', NULL);
INSERT INTO `books` VALUES (6, '花容月下', '一胎二宝：亿万首席爱妻入骨', NULL, '完结', '现代言情', 8.9, '一张百万契约，她成为代孕妈咪。\r\n\r\n　　他是高高在上的帝国首席 执行官，挥手翻云覆雨。\r\n\r\n　　而她却是身世卑微的养女，只因养父事业陷入危机，成\r\n了他的代孕母亲。一朝分娩，哥哥健康，弟弟出来却没了呼吸。她履行了契约，拿着一笔天价的酬金从他眼中消失。\r\n\r\n　　六年后——他仍是万众瞩目的高冷总裁，而她却误打误撞成了他禁锢在笼中的金丝鸟儿！他步步紧逼：“女人，你别以为你能逃得出我的手掌心！”却不想一小奶娃儿横插一脚，小手指着男人老气横秋：“慕雅哲，你最好离她远一点，这是本宝宝的女人！”\r\n\r\n　　男人心惊，只因这小奶包儿的五官，竟与他如出一辙的相似…… ', NULL);
INSERT INTO `books` VALUES (7, '囧囧有妖', '恰似寒光遇骄阳', NULL, '完结', '现代言情', 8.9, '这家伙，口味是有多重，这都下得去口？”一觉醒来，她看着镜子里的自己，爆炸头血腥纹身脸化得像鬼，多看一秒都辣眼睛。重生前，她另有所爱，一心逃离，与他发生关系后对他恨之入骨。重生后，她瞄了眼床上的美色，严肃思考，这事后留下阴影的，貌似应该是他？上一世脑子被门夹了放着绝色老公不要，被渣男贱女所害，被最信任的闺密洗脑，落...', NULL);
INSERT INTO `books` VALUES (8, '囧囧有妖', '许你万丈光芒好', NULL, '完结', '现代言情', 8.9, '“你救了我，我让我爹地以身相许！” 宁夕意外救了只小包子，结果被附赠了一只大包子。 婚后，陆霆骁宠妻如命千依百顺，虐起狗来连亲儿子都不放过。 “老板，公司真给夫人拿去玩？难道夫人要卖公司您也不管？”“卖你家公司了？” “大少爷，不好了！夫人说要把屋顶掀了！”“还不去帮夫人扶梯子。” “粑粑，谢...', NULL);
INSERT INTO `books` VALUES (9, '一路烦花', '夫人，你马甲又掉了', NULL, '连载', '都市言情', 8.9, ' 秦苒，从小在乡下长大，高三失踪一年，休学一年。    一年后，她被亲生母亲接到云城一中借读', NULL);
INSERT INTO `books` VALUES (10, '陌烟', '至尊瞳术师：绝世大小姐', NULL, '连载', '古代言情', 9.0, '24世纪的至尊瞳术师一朝穿越，成了下等小国镇国侯府被废的天才大小姐！修为被废，双眼俱瞎，家族地位被夺？洛清瞳微眯着一双血瞳冷笑：过来！姐教你们做人！一双血瞳，傲世无双！鉴宝透视，医毒破防，无所不能！魂武双修，器药双绝，御兽布阵……她用一生诠释了何谓至尊无双，绝世嚣张！只是万万没想到惹上了一个比她更绝世妖孽的人，从此...', NULL);
INSERT INTO `books` VALUES (11, '花萌种子', '凰临天下：至尊魔神', NULL, '连载', '都市言情', 9.0, '“你……你……你是苏温柔？”“呵~谁说的？请叫我……苏大魔王！”_________当温柔不再，魔女苏醒，性格颠覆，如何玩转世界？____某男人看着面前穿着白色淑女裙的女人疑惑的道：“你是谁？”某伪小白兔胆小弱弱道：“我……我是苏温柔…。”第二日，某男人再次看着一身黑色紧身衣飞檐走壁的黑暗女修罗时，扣住她的肩膀，看着她带着面具的面容果断道：“你是苏温柔。”某真魔女邪魅的勾唇', NULL);
INSERT INTO `books` VALUES (12, '孑与2 ', '明天下', NULL, '连载', '历史军事', 7.0, ' 明末的历史纷乱混杂，堪称是一段由一些有着强大个人魅力的人书写成的历史。 不论是李自成，还是张献忠这些叛逆者，还是崇祯，袁崇焕，这些当权者，亦或是吴三桂，耿精忠这些背叛者，每个人身上都有很多的故事，就是因为有了这些精彩的故事，明末的历史才变得大气起磅礴，波澜起伏。 想要把这一段历史写好，自然要描绘出一个个活生生的人物，不论他的立场如何，我们站在历史长河的边缘上旁观，他们不过是这条长河里的一朵浪花。 冷眼看世界，就是我们目前需要做的事情，冷眼看历史也是我们目前生活的一种追求。 历史长河已经流淌到了我们的脚下，我们大可站在河边，准备迎接我们的历史。', NULL);
INSERT INTO `books` VALUES (13, '猫腻', '庆余年', NULL, '完结', '历史', 8.0, '庆余年简介: 积善之家，必有余庆，留余庆，留余庆，忽遇恩人；幸娘亲，幸娘亲，积得阴功。劝人生，济困扶穷……而谁可知，人生于世，上承余庆，终究却是要自己做出道路抉择，正是所谓岔枝发：东风携云雨，幼藤吐新芽。急催如颦鼓，洗尽茸与华。且待朝阳至，绿遍庭中架。更盼黄叶时，采得数枚瓜。', NULL);
INSERT INTO `books` VALUES (14, '唐七公子', '三生三世十里桃花', NULL, '完结', '仙侠', 8.3, '那一世，大荒之中一处荒山，成就她与他的初见。桃花灼灼，枝叶蓁蓁，妖娆伤眼。记忆可以封存，可心有时也会背叛，忘得了前世情缘，忘不了桃林十里，亦忘不了十里桃林中玄衣的少年。这一世，东海水晶宫，他们不期而遇。不是每个人都能看透这三生三世的爱恨交织，只要你还在，只要我还爱，那么，这世间，刀山火海，毫不畏惧。有些爱，藏在嘴边，挂在心尖。 浮生若梦，情如流水，爱似桃花……', NULL);
INSERT INTO `books` VALUES (15, '无罪', '剑王朝', NULL, '完结', '武侠修仙', 8.2, '自连灭韩、赵、魏三大王朝，大秦王朝已经迎来前所未有之盛世，强大的修行者层出不穷，人人都以身为秦人而荣，但丁宁，一个出身毫无疑问的秦国都长陵普通的市井少年，每天所想的，却是颠覆大秦王朝，杀死修行已至前所未有的第八境的秦皇帝。', NULL);
INSERT INTO `books` VALUES (16, '我会修空调', '我有一座冒险屋 ', NULL, '连载', '悬疑', 7.9, '陈歌继承了失踪父母留下的冒险屋，无奈生意萧条，直到整理冒险屋时意外发现的手机改变了这一切。\r\n　　只要完成手机每日布置的不同难度的任务，冒险屋就能得到修缮甚至扩建！\r\n　　于是陈歌开始在各大禁地里探险取材，将其中场景元素纳入到自己的冒险屋中。随着前来参观的游客们各种惊声尖叫，冒险屋一举成名！然而虽然任务带来的好处越来越多，但其中隐患也慢慢显现，甚至父母失踪的线索似乎也藏其中……', NULL);
INSERT INTO `books` VALUES (17, '纯洁滴小龙', '魔临', NULL, '连载', '悬疑', 7.9, '这个世界一直流传着一个传说：\r\n终有一日，魔王会降临于这个世界，魔王的麾下，有七尊恐怖的魔头，他们，将带给这个世界绝望的黑暗。', NULL);
INSERT INTO `books` VALUES (18, '三天两觉', '惊悚乐园', NULL, '完结', '游戏', 7.8, '这是超越维度的游戏。\r\n　　亦是追寻真理的竞逐。\r\n　　未知的封印，鬼神的赌局……\r\n　　数据的抗争，人类的救赎……\r\n　　在那个连接着现实的虚拟世界——意识决定我们，意识选择我们，意识决定了我们的意识。\r\n　　现在，丢掉你的恐惧。\r\n　　丢掉你的私心杂念、疑问和拒信……解放你的思想。\r\n　　欢迎来到，惊悚乐园。', NULL);
INSERT INTO `books` VALUES (19, '一刀斩斩斩', '我是幕后大佬', NULL, '连载', '游戏', 8.0, '继承了异界老祖遗产的陆无最近很愁。\r\n因为除了府君这个虚名，老祖不但什么都没有留下，甚至原本的下属也全部背叛，瓜分了本属于他的领地。\r\n在这种情况下，陆无依靠老祖研发的神器开始打造游戏服务器，引渡阳间玩家，制作主线任务、副本、剧情、npc等，依靠玩家的力量，开始了自己的征伐之路。', NULL);
INSERT INTO `books` VALUES (20, '水原生', '百寻无一见', NULL, '连载', '现实', 7.8, '我们的故事，终究只能是故事！我们没有在一起，但在存在的无数的平行世界里，总有一对我们是恋人，是情侣，是夫妻！杂笔乱记，无非是些烂熟于心的故事！笔不锋，心不细！只为留住那些琐碎，也为了将遗憾在书中弥补，在想象中变美……我们的故事，兴许也是你和他/她的故事！', NULL);
